import React, { Component } from 'react';

class ToDoForm extends Component {
    state = {
        text: "", 
        category: "Code"
    }

    handleSelect = (e) => {
    console.log(e.target.value);
    }

    state = {
       // username: ''
       // password: ''
    }

    handleChange = e => {
       this.setState({
          [e.target.id]: e.target.value
       })
    }

    render() {
       return (
          <form onSubmit={this.handleSubmit}>
             <input type='text' id='username' value={this.state.username} onChange={this.handleChange}/>
             <input type='text' id='password' value={this.state.password} onChange={this.handleChange}/>
          </form>
       )
    }

    render() {
        return (
            <div>
                <form class="new-task-form">
                    <input onChange={this.handleSelect} type="text" name="text" placeholder="new task detail"></input>
                    <select onChange={this.handleSelect} name="category">
                        <option>Code</option>
                        <option>Food</option>
                        <option>Money</option>
                        <option>Misc</option>
                    </select>
                    <input type="submit" value="Add task"/>
                </form>
            </div>
        );
    }
}

export default ToDoForm;
