import React from 'react';
import { CATEGORIES } from '../data'
import TaskContainer from '../Containers/TaskContainer'
import Categories from './Categories'

class Todo extends React.Component {

  state = {
    tasks: [
      {
        text: 'Buy rice',
        category: 'Food'
      },
      {
        text: 'Save a tenner',
        category: 'Money'
      },
      {
        text: 'Build a todo app',
        category: 'Code'
      },
      {
        text: 'Build todo API',
        category: 'Code'
      },
      {
        text: 'Get an ISA',
        category: 'Money'
      },
      {
        text: 'Cook rice',
        category: 'Food'
      },
      {
        text: 'Tidy house',
        category: 'Misc'
      }
    ], 
    filteredTasks: [],
    categories: CATEGORIES, 
    selected: "All"
  }

  clickHandler = (e) => {
    let button = e.target
    this.setState({
      selected: button.innerText,
      filteredTasks: []
    })
  }

  addTaskHandler = (e) => {
    console.log(e.target)
  }

  render() {

    return (

      <div className="App">
        <h2>My tasks</h2>
        <Categories selected={this.state.selected} categories={this.state.categories} clickHandler={this.clickHandler}/>
        <ToDoForm />
        <TaskContainer tasks={this.state.tasks} selectedCategory={this.state.selected} selectedTasks={this.state.filteredTasks} />
      </div>
    );
  }
}


export default Todo;
